Toolbar1.ChartOnChange:
var v = Value?Value:"";
Grid.Def.Resource.GANTTGanttAvailability = (v.indexOf("Available")>=0?",id#3#R":"") + (v.indexOf("Used")>=0?",id#1#R":"") 
   + (v.indexOf("Available")>=0&&v.indexOf("Used")>=0?",id#8#R":"") 
   + (v.indexOf("Free")>=0?",id#5#R":"") + (v.indexOf("Errors")>=0?",id#6#R":"");
if(!v||v&&!Grid.GetRowById("Resource").Visible) { 
   var F = Grid.GetRows(Grid.Foot); Grid.StartUpdate();
   for(var i=0;i<F.length;i++) if(v) Grid.ShowRow(F[i]); else Grid.HideRow(F[i]); 
   Grid.EndUpdate();
   }
else Grid.RefreshGantt();

Toolbar1.ColsOnChange:
var G = {Name:["Panel","SECTION"], Tasks:["START","END","COMPLETE","DURATION","CLASS","TEXT"], "Tasks 1":["START1","END1","DURATION1","PARTS1","CLASS1","TEXT1"],
   Dependencies:["DESCENDANTS","ANCESTORS"], Flags:["FLAGS","FLAGSTEXT"], Constraints:["MINSTART","MINEND","MAXSTART","MAXEND"], 
   Calendar:["CALENDAR"], Resources: ["RESOURCES","PRICE"], Texts:["TEXT","TEXT1","FLAGSTEXT"], Slack:["SLACK"], Gantt:["GANTT"] };
var S = ["id"], H = ["PROGRESS"], v = Value?Value:"";
if(Grid.Cols.TASK.Visible) S = S.concat("TASK"); else H = H.concat("TASK");
for(var g in G) if(v.indexOf(g)>=0) S = S.concat(G[g]); else H = H.concat(G[g]);
Grid.ChangeColsVisibility(S,H,1);
